var gulp=require("gulp"),spritesmith=require("gulp.spritesmith");gulp.task("sprite",function(){var s=gulp.src("img/common/*.*").pipe(spritesmith({imgName:"sprite.png",imgPath:"../img/sprite.png",cssName:"sprite.less",cssFormat:"less",algorithm:"binary-tree"}));s.img.pipe(gulp.dest("img/")),s.css.pipe(gulp.dest("less/"))}),gulp.task("sprite-retina",function(){var s=gulp.src("img/retina/*.*").pipe(spritesmith({imgName:"sprite-retina.png",imgPath:"../img/sprite-retina.png",cssName:"sprite-retina.less",cssFormat:"less",algorithm:"binary-tree"}));s.img.pipe(gulp.dest("img/")),s.css.pipe(gulp.dest("less/"))}),gulp.task("default",["sprite","sprite-retina"]);